$(document).on("turbolinks:load", function(){
  clickAddBarListener();
  submitFormForJsonListener();
});

var clickAddBarListener = function(){
  $('#add-bar-link').on("ajax:success", function(event, response){
    $('#form-holder').append(response);
  });
};

//  #new_bar is an id that is generated by form_for
var submitFormForJsonListener = function(){
  $("#form-holder").on("ajax:success", "#new_bar", function(event, response){
    console.log(response);
  });
};
